package test;

public class loopingexample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 1;
		while (num<11)
		{
			num=num+1;
		System.out.println(num);
		}
		System.out.println("end of while loop");	
		
		int num1=10;
		while(num1>0) {
			
		
		System.out.println(num1);
		num1--;
		
		}
		System.out.println("end of while loop");
	}
}

