package test;

public class stringexample1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name= "Anoop";
		String name1="Ehaan is a naughty boy";
		System.out.println("num of chars in string is "+name.length());
		System.out.println("the 4th chars in the string is "+name1.charAt(3));
		System.out.println("the last chars in the string is "+name1.charAt(name1.length()-1));
		System.out.println("the midlle chars in the string is "+name.charAt(name.length()/2));
		System.out.println("the string is in uppercase is "+name1.toUpperCase());
		
		

	}

}
